import React, {useState, useEffect} from 'react';
import api from './api';
import './styles.css';

import {
  Input,
  Button,
  Form,
  FormGroup,
  Label,
  Container,
  Row,
  Col
} from "reactstrap";

function App() {

  const [val1, setVal1] = useState(0);
  const [val2, setVal2] = useState(0);
  const [result, setResult] = useState(0);

  async function sum(e) {
    e.preventDefault();
    const data = { val1, val2}
    try { 
      const response = await api.post('add', data ); 
      setResult(response.data.result);
    } 
    catch (error) { 
      alert('Erro ao realizar a operação de soma, tente novamente!'); 
    }
  }

  async function sub(e) {
    e.preventDefault();
    const data = { val1, val2}
    try { 
      const response = await api.post('sub', data ); 
      console.log(response);
      setResult(response.data.result);

    } 
    catch (error) { 
      alert('Erro ao realizar a operação de subtração, tente novamente!'); 
    }
  }

  async function mult(e) {
    e.preventDefault();
    const data = { val1, val2}
    try { 
      const response = await api.post('mult', data ); 
      setResult(response.data.result);
    } 
    catch (error) { 
      alert('Erro ao realizar a operação de multiplicação, tente novamente!'); 
    }
  }

  async function div(e) {
    e.preventDefault();
    const data = { val1, val2}
    try { 
      const response = await api.post('div', data ); 
      setResult(response.data.result);
    } 
    catch (error) { 
      alert('Erro ao realizar a operação de divisão, tente novamente!'); 
    }
  }

  async function pot(e) {
    e.preventDefault();
    const data = { val1, val2}
    try { 
      const response = await api.post('pow', data ); 
      setResult(response.data.result);
    } 
    catch (error) { 
      alert('Erro ao realizar a operação de potênciação, tente novamente!'); 
    }
  }
  return (
    {/* <>
    // <Container id="example-2" fullHeight className="bg-dark">
    //       <Row fullHeight className="bg-white" alignItems="center">
    //         <Col className="bg-white">
    //         <Form inline>
    //           <h2 className="text-center title">Calculadora</h2>
    //           <FormGroup className="mb-2 mr-sm-2 mb-sm-0">
    //             <Label for="exampleEmail" className="mr-sm-2">Valor 01</Label>
    //             <Input type="email" name="email" id="exampleEmail" placeholder="something@idk.cool" />
    //           </FormGroup>
    //           <FormGroup className="mb-2 mr-sm-2 mb-sm-0">
    //             <Label for="examplePassword" className="mr-sm-2">Password</Label>
    //             <Input type="password" name="password" id="examplePassword" placeholder="don't tell!" />
    //           </FormGroup>
    //           <Button>Submit</Button>
    //         </Form>
    //         </Col>
    //       </Row>
    //     </Container>
     // <div className="bg-dark">
    // <Container fluid="lg" className="text-center">
    //   <Row className="bg-dark">
    //     <Col className="text-center"> 

    //     <h1 className="title">Calculadora</h1>
    //     </Col>
    //   </Row>

    // </Container> */},
  <div className="container">
    <div className="">        
      <h1 className="title">CALC</h1>

        <Input
          type="number"
          onChange={e => setVal1(e.target.value)}
          placeholder="Insira o primeiro valor"
          required
        />
        <Input 
          type="number" 
          onChange={e => setVal2(e.target.value)} 
          placeholder="Insira o segundo valor"
          required
        />

        <Row>
          <Col>

          </Col>
        </Row>

        <div className="content-div">
          <Button onClick={e => sum(e)}> Somar</Button>
          <Button onClick={e => sub(e)}> Subtrair</Button>
          <Button onClick={e => div(e)}> Divisão</Button>
          <Button onClick={e => mult(e)}> Multiplicação</Button>
          <Button onClick={e => pot(e)}> Potência</Button>
        </div>
        <div style={{display: 'flex',justifyContent: 'center', margin: '5px'}}>
          <Input 
            type="text" 
            className="result"
            value={result}
            disabled
            placeholder="Resultado"
          />
        </div>
      </div>
  </div>
    // </>
  );
}

export default App;
