import React, {useState} from 'react';
import api from './api';
import './styles.css';

import {
  Input,
  Button,
  Form,
  FormGroup,
  Label,
  Container,
  Row,
  Col
} from "reactstrap";

function App() {

  const [val1, setVal1] = useState(0);
  const [val2, setVal2] = useState(0);
  const [result, setResult] = useState(0);

  async function soma(e) {
    e.preventDefault();
    const data = { val1, val2}
    try { 
      const response = await api.post('sum', data ); setResult(response.data.result);
    } 
    catch (error) { 
      alert('Erro ao realizar a operação de soma, tente novamente!'); 
    }
  }

  async function sub(e) {
    e.preventDefault();
    const data = { val1, val2}
    try { 
      const response = await api.post('sub', data ); setResult(response.data.result);
    } 
    catch (error) { 
      alert('Erro ao realizar a operação de subtração, tente novamente!'); 
    }
  }

  async function mult(e) {
    e.preventDefault();
    const data = { val1, val2}
    try { 
      const response = await api.post('mult', data ); setResult(response.data.result);
    } 
    catch (error) { 
      alert('Erro ao realizar a operação de multiplicação, tente novamente!'); 
    }
  }

  async function div(e) {
    e.preventDefault();
    const data = { val1, val2}
    try { 
      const response = await api.post('div', data ); setResult(response.data.result);
    } 
    catch (error) { 
      alert('Erro ao realizar a operação de divisão, tente novamente!'); 
    }
  }

  async function pot(e) {
    e.preventDefault();
    const data = { val1, val2}
    try { 
      const response = await api.post('pow', data ); setResult(response.data.result);
