import React, {useState} from 'react';
import api from './api';
import './styles.css';

import {
  Input,
  Button,
  Container,
  Row,
  Col
} from "reactstrap";

function App() {
  const [val1, setVal1] = useState(0);
  const [val2, setVal2] = useState(0);
  const [result, setResult] = useState(0);

  async function soma(e) {
    e.preventDefault();
    const data = { val1, val2}
    try { const response = await api.post('soma', data ); setResult(response.data.result);} 
    catch (error) { alert('Falha na operação, tente novamente'); }
  }

  async function sub(e) {
    e.preventDefault();
    const data = { val1, val2}
    try { const response = await api.post('sub', data ); setResult(response.data.result);} 
    catch (error) { alert('Falha na operação, tente novamente'); }
  }

  async function mult(e) {
    e.preventDefault();
    const data = { val1, val2}
    try { const response = await api.post('mult', data ); setResult(response.data.result);} 
    catch (error) { alert('Falha na operação, tente novamente'); }
  }

  async function div(e) {
    e.preventDefault();
    const data = { val1, val2}
    try { const response = await api.post('div', data ); setResult(response.data.result);} 
    catch (error) { alert('Falha na operação, tente novamente'); }
  }

  async function pot(e) {
    e.preventDefault();
    const data = { val1, val2}
    try { const response = await api.post('pot', data ); setResult(response.data.result);} 
    catch (error) { alert('Falha na operação, tente novamente'); }
  }
  return (
    <div>
  <Container fluid="lg">
    <Row>
      <Col></Col>
    </Row>

  </Container>
  <div className="container">
    <div className="calculator">        
      <h1>Calculadora</h1>

        <Input
          type="text"
          onChange={e => setVal1(e.target.value)}
          placeholder="Primeiro valor"
        />
        <Input 
          type="text" 
          onChange={e => setVal2(e.target.value)} 
          placeholder="Segundo valor"
        />

        <div className="content-div">
          <Button onClick={e => soma(e)}> Somar</Button>
          <Button onClick={e => sub(e)}> Subtrair</Button>
          <Button onClick={e => div(e)}> Divisão</Button>
          <Button onClick={e => mult(e)}> Multiplicação</Button>
          <Button onClick={e => pot(e)}> Potência</Button>
        </div>
        <div style={{display: 'flex',justifyContent: 'center', margin: '5px'}}>
          <Input 
            type="text" 
            className="result"
            value={result}
            disabled
            placeholder="Resultado"
          />
        </div>
      </div>
    </div>
    </div>
  );
}

export default App;
