import React, {useState} from 'react';
import api from './api';
import './styles.css';

import {
  Input,
  Button
} from "reactstrap";

function App() {
  const [val1, setVal1] = useState(0);
  const [val2, setVal2] = useState(0);
  const [result, setResult] = useState(0);

  async function soma(e) {
    e.preventDefault();
    const data = { val1, val2}
    try { setResult(await api.post('soma', data ));} 
    catch (error) { alert('Falha na operação, tente novamente'); }
  }

  async function sub(e) {
    e.preventDefault();
    const data = { val1, val2}
    try { setResult(await api.post('sub', data ));} 
    catch (error) { alert('Falha na operação, tente novamente'); }
  }

  async function mult(e) {
    e.preventDefault();
    const data = { val1, val2}
    try { setResult(await api.post('mult', data ));} 
    catch (error) { alert('Falha na operação, tente novamente'); }
  }

  async function div(e) {
    e.preventDefault();
    const data = { val1, val2}
    try { setResult(await api.post('div', data ));} 
    catch (error) { alert('Falha na operação, tente novamente'); }
  }

  async function pot(e) {
    e.preventDefault();
    const data = { val1, val2}
    try { setResult(await api.post('pot', data ));} 
    catch (error) { alert('Falha na operação, tente novamente'); }
  }
  return (
    <>
      <div className="calculator">
          <Input type="text" onChange={e => setVal1(e.target.value)} />
          <input type="text" onChange={e => setVal2(e.target.value)} />
          <input type="button" onclick={() => soma()} />
          <input type="button" onclick={() => sub()} />
          <input type="button" onclick={() => div()} />
          <input type="button" onclick={() => mult()} />
          <input type="button" onclick={() => pot()} />
      </div>
    </>
  );
}

export default App;
